'use strict'

const loopify = require('./index.js')

const assert = (a, b) => {
    if (a !== b) {
        console.log(`test failed: "${a}" `)
        process.exit(1)
    }
}

let i = 5
assert('', loopify.times(0, () => (i += 3)).join())
assert('8', loopify.times(1, () => (i += 3)).join())
assert('11,14', loopify.times(2, () => (i += 3)).join())
assert('17,20,23', loopify.times(3, () => (i += 3)).join())

assert('', loopify.range(-1).join())
assert('', loopify.range(0).join())
assert('0', loopify.range(1).join())
assert('0,1', loopify.range(2).join())

assert('', loopify.range(-2, -2).join())
assert('-2', loopify.range(-2, -1).join())
assert('-2,-1', loopify.range(-2, 0).join())
assert('-2,-1,0', loopify.range(-2, 1).join())

assert('', loopify.range(0, -1).join())
assert('', loopify.range(0, 0).join())
assert('0', loopify.range(0, 1).join())

assert('0', loopify.range(0, 1, 2).join())
assert('0', loopify.range(0, 2, 2).join())
assert('0,2', loopify.range(0, 3, 2).join())

assert('1', loopify.range(1, 0, -2).join())
assert('2', loopify.range(2, 0, -2).join())
assert('3,1', loopify.range(3, 0, -2).join())
